  //       if (this.state.editing) {
  //         this.cancel();
  //       } else {
  //         this.edit(e,id);
  //       }
  //     };

  //     edit = (e,id) => {
  //       this.setState({
  //         editing: true
  //       }, () => {
  //         this.domElm.focus();
  //       });
  //     };

  //     save = () => {
  //       axios.put(`/api/v1/todos/${id}`, {todo: {title: e.target.value}})
  //       .then(response => {
  //         const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
  //         const todos = update(this.state.todos, {
  //           [todoIndex]: {$set: response.data}
  //         })
  //         this.setState({
  //           todos: todos,
  //           editable: false
  //         })
  //       })
  //       .catch(error => console.log(error))
  //     };

  //     cancel = () => {
  //       this.setState({
  //         editing: false
  //       });
  //     };

  //     isValueChanged = () => {
  //       return this.props.value !== this.domElm.textContent
  //     };

  //     handleKeyDown = (e) => {
  //       const { key } = e;
  //       switch (key) {
  //         case 'Enter':
  //         case 'Escape':
  //           this.save();
  //           break;
  //       }
  //     };

  //     render() {
  //       let editOnClick = true;
  //       const {editing} = this.state;
  //       if (this.props.editOnClick !== undefined) {
  //         editOnClick = this.props.editOnClick;
  //       }
  //       return (
  //         <WrappedComponent
  //           className={editing ? 'editing' : ''}
  //           onClick={(e,id) => !editing ? this.toggleEdit : undefined}
  //           contentEditable={editing}
  //           ref={(domNode) => {
  //             this.domElm = domNode;
  //           }}
  //           onBlur={this.save}
  //           onKeyDown={this.handleKeyDown}
  //           onChange={this.handleChange}
  //           {...this.props}
  //       >
  //         {this.props.value}
  //       </WrappedComponent>
  //       )
  //     }
  //   }
  // }

  getTodos() {
    axios.get('/api/v1/todos')
    .then(response => {
      this.setState({todos: response.data})
    })
    .catch(error => console.log(error))
  }

  createTodo = (e) => {
    if (e.key === 'Enter' && !(e.target.value === '')) {
      axios.post('/api/v1/todos', {todo: {title: e.target.value}})
      .then(response => {
        const todos = update(this.state.todos, {
          $splice: [[0, 0, response.data]]
        })
        this.setState({
          todos: todos,
          inputValue: ''
        })
      })
      .catch(error => console.log(error))      
    }    
  }

  updateTodo = (e, id) => {
    axios.put(`/api/v1/todos/${id}`, {todo: {done: e.target.checked}})
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
      const todos = update(this.state.todos, {
        [todoIndex]: {$set: response.data}
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))      
  }

  // editTodo = (e, id) => {
  //   if (e.key === 'Enter' && !(e.target.value === '')) {
  //     axios.put(`/api/v1/todos/${id}`, {todo: {title: e.target.value}})
  //     .then(response => {
  //       const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
  //       const todos = update(this.state.todos, {
  //         [todoIndex]: {$set: response.data}
  //       })
  //       this.setState({
  //         todos: todos,
  //         editable: false
  //       })
  //     })
  //     .catch(error => console.log(error))
  //   }      
  // }

  deleteTodo = (id) => {
    axios.delete(`/api/v1/todos/${id}`)
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === id)
      const todos = update(this.state.todos, {
        $splice: [[todoIndex, 1]]
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))
  }

  handleChange = (e) => {
    this.setState({inputValue: e.target.value});
  }

  // handleEdit = () => {
  //   this.setState({editable: !this.state.editable})
  // }

  componentDidMount() {
    this.getTodos()
  }

  render() {
    console.log(this.props);
    // let EditableLabel = contentEditable('label');

    return (
      <React.Fragment>
        <p>Write task below:</p>
        <div style={"padding: 10px 50px 10px"}>
          <div className="inputContainer">
            <input className="taskInput" type="text" 
              placeholder="Add a task" maxLength="50" 
              onKeyPress={this.createTodo} 
              value={this.state.inputValue} onChange={this.handleChange}/>
          </div>  	    
          <div className="listWrapper">
            <ul className="taskList">
            {this.state.todos.map((todo) => {
              // let editableTodo = todo.editable
              //   ? <input type='text' 
              //       defaultValue={todo.title} 
              //       value={todo.inputValue} 
              //       onKeyPress={this.editTodo} 
              //       onChange={this.handleChange}/>
              //   : <label className="taskLabel">{todo.title}</label>;

              return(
                <li className="task" todo={todo} key={todo.id}>
                  <input className="taskCheckbox" type="checkbox" 
                  checked={!!todo.done}
                  onChange={(e) => this.updateTodo(e, todo.id)}/>      
                  <label className="taskLabel">{todo.title}</label>
                  {/* <EditableLabel value={todo.title}/> */}
                  {/* <button className="editTaskBtn" onClick={() => this.handleEdit()}>{this.state.editable ? 'Submit' : 'Edit'}</button> */}
                  <span className="deleteTaskBtn"
                    onClick={(e) => this.deleteTodo(todo.id)}>
                      x
                  </span>
                </li>
                )       
              })} 	 
            </ul>
          </div>
        </div>    
      </React.Fragment>
    );
  }
}

export default Main
