import React from "react"
import update from "immutability-helper"
import axios from "axios"
// import PropTypes from "prop-types"
// import TodosContainer from "./TodosContainer"
// import AllTodos from "./AllTodos";

// higher order function to make a tag inline-editable
function contentEditable(WrappedComponent) {
  return class extends React.Component {
    state = {
      editing: false
    }

    toggleEdit = (e) => {
      e.stopPropagation();
      if (this.state.editing) {
        this.cancel();
      } else {
        this.edit();
      }
    };

    edit = () => {
      this.setState({
        editing: true
      }, () => {
        this.domElm.focus();
      });
    };

    save = () => {
      this.setState({
        editing: false
      }, () => {
        if (this.props.onSave && this.isValueChanged()) {
          console.log('Value is changed', this.domElm.textContent);
        }
      });
    };

    cancel = () => {
      this.setState({
        editing: false
      });
    };

    isValueChanged = () => {
      return this.props.value !== this.domElm.textContent
    };

    handleKeyDown = (e) => {
      const { key } = e;
      switch (key) {
        case 'Enter':
        case 'Escape':
          this.save();
          break;
      }
    };

    render() {
      let editOnClick = true;
      const {editing} = this.state;
      if (this.props.editOnClick !== undefined) {
        editOnClick = this.props.editOnClick;
      }
      return (
        <WrappedComponent
          className={editing ? 'editing' : ''}
          onClick={!editing ? this.toggleEdit : undefined}
          contentEditable={editing}
          ref={(domNode) => {
            this.domElm = domNode;
          }}
          onBlur={this.save}
          onKeyDown={this.handleKeyDown}
          {...this.props}
      >
        {this.props.value}
      </WrappedComponent>
      )
    }
  }
}

class Main extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      inputValue: '',
      editable: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  getTodos() {
    axios.get('/api/v1/todos')
    .then(response => {
      this.setState({todos: response.data})
    })
    .catch(error => console.log(error))
  }

  createTodo = (e) => {
    if (e.key === 'Enter' && !(e.target.value === '')) {
      axios.post('/api/v1/todos', {todo: {title: e.target.value}})
      .then(response => {
        const todos = update(this.state.todos, {
          $splice: [[0, 0, response.data]]
        })
        this.setState({
          todos: todos,
          inputValue: ''
        })
      })
      .catch(error => console.log(error))      
    }    
  }

  updateTodo = (e, id) => {
    axios.put(`/api/v1/todos/${id}`, {todo: {done: e.target.checked}})
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
      const todos = update(this.state.todos, {
        [todoIndex]: {$set: response.data}
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))      
  }

  deleteTodo = (id) => {
    axios.delete(`/api/v1/todos/${id}`)
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === id)
      const todos = update(this.state.todos, {
        $splice: [[todoIndex, 1]]
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))
  }

  handleChange = (e) => {
    this.setState({inputValue: e.target.value});
  }

  handleEdit(){
    this.setState({
      editable: !this.state.editable
    })
  }

  componentDidMount() {
    this.getTodos()
  }

  render() {
    console.log(this.props);
    // let EditableLabel = contentEditable('label');

    <React.Fragment>
    let editableTodo = this.state.editable
      ? <input type='text' defaultValue={todo.title} value={this.state.inputValue} onChange={this.handleChange}/>
      : <label className="taskLabel">{todo.title}</label>
    </React.Fragment>

    return (
      <React.Fragment>
        <p>Write task below:</p>
        <div>
          <div className="inputContainer">
            <input className="taskInput" type="text" 
              placeholder="Add a task" maxLength="50" 
              onKeyPress={this.createTodo} 
              value={this.state.inputValue} onChange={this.handleChange}/>
          </div>  	    
          <div className="listWrapper">
            <ul className="taskList">
            {this.state.todos.map((todo) => {
              return(
                <li className="task" todo={todo} key={todo.id}>
                  <input className="taskCheckbox" type="checkbox" 
                  checked={!!todo.done}
                  onChange={(e) => this.updateTodo(e, todo.id)}/>              
                  {editableTodo}
                  <span className="editTaskBtn" onClick={() => this.handleEdit()}>{this.state.editable ? 'Submit' : 'Edit'}</span>
                  <span className="deleteTaskBtn"
                    onClick={(e) => this.deleteTodo(todo.id)}>
                      x
                  </span>
                </li>
                )       
              })} 	 
            </ul>
          </div>
        </div>    
      </React.Fragment>
    );
  }
}

export default Main
