import React from "react"
import update from "immutability-helper"
import axios from "axios"
import "../../assets/stylesheets/todos.scss"
// import "boostrap/dist/css/boostrap.css"
// import PropTypes from "prop-types"
// import TodosContainer from "./TodosContainer"
// import AllTodos from "./AllTodos";
import Header from "./Header"

class Main extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [],
      inputValue: '',
      editValue: '',
      editing: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }

  getTodos() {
    axios.get('/api/v1/todos')
    .then(response => {
      this.setState({todos: response.data})
    })
    .catch(error => console.log(error))
  }

  createTodo = (e) => {
    if (e.key === 'Enter' && !(e.target.value === '')) {
      axios.post('/api/v1/todos', {todo: {title: e.target.value}})
      .then(response => {
        const todos = update(this.state.todos, {
          $splice: [[0, 0, response.data]]
        })
        this.setState({
          todos: todos,
          inputValue: ''
        })
      })
      .catch(error => console.log(error))      
    }    
  }

  // handles updating checkboxes
  updateTodo = (e, id) => {
    axios.put(`/api/v1/todos/${id}`, {todo: {done: e.target.checked}})
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
      const todos = update(this.state.todos, {
        [todoIndex]: {$set: response.data}
      })
      this.setState({
        todos: todos
      })
    })
    .then(console.log(this.state.todos))
    .catch(error => console.log(error))      
  }

  // handles editing todo title
  editTodo = (e, id) => {
    if (e.key === 'Enter' && !(e.target.value === '')) {
      axios.put(`/api/v1/todos/${id}`, {todo: {title: e.target.value, editable: false}})
      .then(response => {
        const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
        const todos = update(this.state.todos, {
          [todoIndex]: {$set: response.data}
        })
        this.setState({
          todos: todos,
          editing: false,
          editValue: ''
        })
      })
      .then(console.log(this.state.todos))
      .catch(error => console.log(error))
    }      
  }

  deleteTodo = (id) => {
    axios.delete(`/api/v1/todos/${id}`)
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === id)
      const todos = update(this.state.todos, {
        $splice: [[todoIndex, 1]]
      })
      this.setState({
        todos: todos
      })
    })
    .catch(error => console.log(error))
  }

  handleChange = (e) => {
    this.setState({inputValue: e.target.value});
  }

  handleEdit = (id) => {
    this.setState({
      editing: !this.state.editing
    })
    console.log(this.state.editing);

    axios.put(`/api/v1/todos/${id}`, {todo: this.state.editing ? {editable: true} : {editable: false}})
    .then(response => {
      const todoIndex = this.state.todos.findIndex(x => x.id === response.data.id)
      const todos = update(this.state.todos, {
        [todoIndex]: {$set: response.data}
      })
      this.setState({
        todos: todos
      })
    })
    .then(console.log(this.state.todos))
    .catch(error => console.log(error))      
  }

  componentDidMount() {
    this.getTodos()
  }

  render() {
    console.log(this.props);

    return (
      <React.Fragment>
        <Header heading="My To-do List" />
        <div>
          <div className="inputContainer">
            <input className="taskInput" type="text" 
              placeholder="Add a task" maxLength="50" 
              onKeyPress={this.createTodo} 
              value={this.state.inputValue} onChange={this.handleChange}/>
          </div>  	    
          <div className="listWrapper">
            <ul className="taskList">
            {this.state.todos.map((todo) => {

              return(
                <li className="task" todo={todo} key={todo.id}>
                  <input className="taskCheckbox" type="checkbox" 
                  checked={!!todo.done}
                  onChange={(e) => this.updateTodo(e, todo.id)}/>      
                  { todo.editable
                  ? <input className="editForm" defaultValue={todo.title}
                    onKeyPress={this.editTodo(e, todo.id)}
                    value={this.state.editValue} onChange={this.handleEditChange}></input>
                  : <label className="taskLabel">{todo.title}</label>}
                  <span className="deleteTaskBtn"
                    onClick={(e) => this.deleteTodo(todo.id)}>
                      x
                  </span>
                  <span className="editTaskBtn" onClick={() => this.handleEdit(todo.id)}>{todo.editable ? 'Submit' : '✎'}</span>
                </li>
                )       
              })} 	 
            </ul>
          </div>
        </div>    
      </React.Fragment>
    );
  }
}

export default Main
