import React from "react"
import PropTypes from "prop-types"
import update from "immutability-helper"
import axios from "axios"
import Header from "./Header"
import "../../assets/stylesheets/todos.scss"

class TagManager extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tags: [],
      todos: [],
      inputValue: '',
    };

    this.handleChange = this.handleChange.bind(this);

  }

  getTodos() {
    axios.get('/api/v1/todos')
    .then(response => {
      this.setState({todos: response.data})
    })
    .catch(error => console.log(error));
  }

  getTags() {
    axios.get('/api/v1/tags')
    .then(response => {
      this.setState({tags: response.data})
    })
    .catch(error => console.log(error));
  }

  createTag = (e) => {
    if (e.key === 'Enter' && !(e.target.value === '')) {
      axios.post('/api/v1/tags', {tag: {name:e.target.value}})
      .then(response => {
        const tags = update(this.state.tags, {
          $splice: [[0, 0, response.data]]
        });
        this.setState({
          tags: tags,
          inputValue: ''
        });
      })
      .then(console.log("updated tags: ", this.state.tags))
      .catch(error => console.log(error));
    }
  }

  deleteTag = (id) => {
    axios.delete(`/api/v1/tags/${id}`)
    .then(response => {
      const tagIndex = this.state.tags.findIndex(x => x.id === id)
      const tags = update(this.state.tags, {
        $splice: [[tagIndex, 1]]
      })
      this.setState({
        tags: tags
      })
    })
    .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  componentDidMount() {
    this.getTags();
    this.getTodos();
  }


  render () {
    console.log(this.props);
    return (
      <React.Fragment>
        <Header heading="Tag List" />
        <ul>
          <a href="../">Back to List</a>
        </ul>

        <div className="inputContainer">
          <input className="tagInput" type="text" 
            placeholder="Add a new tag" maxLength="50" 
            name="inputValue"
            onKeyPress={this.createTag} 
            value={this.state.inputValue} onChange={this.handleChange}/>
        </div> 

        <div className="listWrapper">
          <ul className="taskList">
            {this.state.tags.map((tag) => {
              return (
                <li className="task" tag={tag} key={tag.id}>
                  <label className="taskLabel">{tag.name}
                    <span className="deleteTaskBtn"
                      onClick={(e) => this.deleteTag(tag.id)}>
                        x
                    </span>
                  </label>
                  {this.state.todos.map((todo) => 
                    {if (todo.tags.id === tag.id) {
                      return todo.title;
                    }})}
                </li>
              )
            })}
          </ul>
        </div>
      </React.Fragment>
    );
  }
}

export default TagManager
